mapper.select:
select billid erp_id, no no, DELIVERYTRANSFERTYPE arrival_logistics_arrangement
from scm_hx03_rohead
where billid=#{orderId}

select r.billdtlid as erp_billdtl_id, r.billid as erp_id, row_number() over(partition by r.billid order by r.billdtlid) as line_number, ro.no as order_no,
r.dtl_ItemType as item_type, r.dtl_ItemID as item_id, good.item_no as item_no, r.dtl_ItemDescr as item_name, good.item_type_code as item_type_code, good.item_type_name as item_type_name, good.item_model as item_model, good.item_brand as item_brand, good.item_brand_name as item_brand_name,
r.dtl_ItemQty as item_quantity, r.dtl_itemuom as item_unit, r.dtl_DeliveryDate as approach_date, r.dtl_leaseTerm as lease_term, r.dtl_LeaseUnit as lease_unit, r.dtl_SettleModel as settle_method, r.dtl_DepositPrice as deposit_unit_price, r.dtl_PriceMode as price_mode, r.dtl_StdPrice as standard_rent, r.dtl_Discount as discount_rate, r.dtl_Price as lease_term_price, r.dtl_Notes as remark, r.dtl_No as dtl_no, r.dtl_MatConvRate as material_conversion_rate, r.SrcDtlID as tracking_id, r.dtl_CreditPrice as credit_price, r.dtl_SelectItem as define_item, r.dtl_StdAmount as standard_rent_amount, r.Dtl_DIscountAmount as discount_rent_amount, r.dtl_StdPrice_DtlID as standard_amount_id, r.dtl_CreditPrice_DtlID as credit_price_id, r.dtl_DepositAmt as deposit_amount, r.dtl_PriceUnit as lease_term_price_unit, r.dtl_StdPriceUnit as standard_rent_unit, r.dtl_DepositPriceUnit as deposit_unit, r.dtl_CreditPriceUnit as credit_price_unit, r.DTL_INIT_DATA as init_data, r.DTL_INIT_DATA1 as init_data_time, r.dtl_DirectPrice as rantal_price, r.dtl_DirectPriceUnit as rantal_price_unit, r.dtl_OringalQty as original_quantity, r.dtl_messAge as waring_message, r.dtl_chkChange as dtl_chkChange, r.dtl_IntStdPrice as inner_settle_standard_amount, r.dtl_IntDiscount as inner_settle_discount, r.dtl_SignItemID as sign_item_id, r.dtl_MinLease as min_lease_cycle, r.dtl_MinRentAmount as min_rent_amount, r.dtl_sourceDtlID as source_dtlId, r.dtl_isStepPrice as has_step_price, r.Dtl_SupplementQty as supplement_quantity, r.dtl_SupplementTime as supplement_lease_term, r.dtl_SupplementPrice as supplement_lease_price, r.dtl_DdeliveryDate as delivery_date_end, r.dtl_AdvancePrice as advance_price, r.dtl_AdvancePriceUnit as advance_price_unit, r.chengji as total_advance from SCM_HX03_RO_Dtl r left join scm_hx03_rohead ro on r.billid=ro.billid left join ( select 11 as goodType, m.id as id, m.code as item_no, b1.code as item_type_code, b1.name as item_type_name, m.EquSpec as item_model, b.code as item_brand, b.name as item_brand_name from CP_HX01_EquModel m left join CP_HX01_EquModel b on m.parentid=b.id left join CP_HX01_EquModel b1 on b.parentid=b1.id union select 20 as goodType, m.id as id, m.code as item_no, b1.code as item_type_code, b1.name as item_type_name, m.Spec as item_model, b.code as item_brand, b.name as item_brand_name from CP_HX00_Material m left join CP_HX00_Material b on m.parentid=b.id left join CP_HX00_Material b1 on b.parentid=b1.id )good on good.id = r.dtl_ItemID and dtl_ItemType=good.goodType where r.billid=#{orderId}
